function imageviewer(selector = 'img')
{
	selector = $(selector);

	selector.click(function() {
		
		let imageLink = $(this).attr('src');

		if (imageLink === '') return console.warn('[ImageViewer] Invalid link to image');
        $("#img-view-light").empty();
		$('<div>', {
			id:    'imageviewer-wrapper',
			css: {
				position:        'fixed',
				top:             '0',
				left:            '0',
				zIndex:          '99999',
				display:         'flex',
				justifyContent:  'center',
				alignItems:      'center',
				width:           '100%',
				height:          '100vh',
                backgroundColor: 'rgba(0,0,0,0.5)',
                overflow        :  'scroll'  
			},
			append: $('<img>', {
				id:  'imageviewer-image',
				src: imageLink,
			}).add($('<div>', {
				id:  'imageviewer-close',
				css: {
					position:           'fixed',
					top:                '0',
					right:              '0',
					zIndex:             '100000',
					width:              '40px',
					height:             '40px',
					padding:            '20px',
					backgroundColor:    'rgba(0,0,0,0.5)',
					cursor:             'pointer',
					translation:        '0.5s ease',
				},
				append: $('<div>', {
					css: {
						width:              '100%',
						height:             '100%',
						float:              'right',						
						backgroundPosition: 'center center',
                        backgroundSize:     'cover',
                        color:              'red',
                        fontSize:           '30px',
                        right:              '-9px',
                        position:           'absolute',
                        top:                '4px'
                    },
                    class:'fa fa-close'
				}),
				on: {
					click: function(event) {
						$('#imageviewer-wrapper').remove();
					}
				}
			})),
		}).appendTo('#img-view-light');

	});
}